export default {
	storeValues : (name,address,phone_numbers,activity_ids,id,cuisins,image_url,zomato_url,price_range) => {
		storeValue('name',name);
		storeValue('address',address);
		storeValue('phone_numbers',Utilities.getText(phone_numbers));
		storeValue('activity_ids',activity_ids);
		storeValue('id',id);
		storeValue('cuisins',cuisins);
		storeValue('image_url',image_url);
		storeValue('zomato_url',zomato_url);
		storeValue('price_range',price_range);
	},
	getText: (arrayForText) => {
		if(arrayForText.length===0)return "";
		let result = arrayForText[0];
		for(let i=1;i<arrayForText.length;i++){
			result += ",";
			result += arrayForText[i];
		}
		return result;
	},
	getUUID: () => {
		var result, i, j;
		result = '';
		for(j=0; j<32; j++) {
			if( j == 8 || j == 12 || j == 16 || j == 20)
				result = result + '-';
			i = Math.floor(Math.random()*16).toString(16).toUpperCase();
			result = result + i;
		}
		return result.toLowerCase();
	},
	closeModalAndRefresh: async(modalSel)=>{
		const data = await GetRestaurants.run();
		if(data){
			closeModal(modalSel);
		}
	}
}
